\documentclass{standalone}
\usepackage{pgfplots}
\pgfplotsset{compat=1.18}

\begin{document}
\begin{tikzpicture}
\begin{axis}[
    domain=-2*pi:2*pi,
    samples=201, % 201 points to achieve step size of 0.1
    samples y=0,
    xlabel=$x$,
    ylabel=$\sin(x)$,
    xmin=-2*pi, xmax=2*pi,
    ymin=-1.5, ymax=1.5,
    axis lines=middle,
    width=10cm,
    height=6cm,
    grid=major,
    clip=false
]
\addplot[blue, thick, line width=1.5pt] {sin(x)};
\end{axis}
\end{tikzpicture}
\end{document}
